<?phpclass Person extends CI_Controller {	public function index()	{		$this->load->view('person/index');	}	public function view_all()	{		$this->load->model('person_model');		$data['persons'] = $this->person_model->get_all_persons();		$this->load->view('person/view_all',$data);	}		public function search()	{		if($this->input->post())		{			$this->load->model('person_model');			$data['persons'] =$this->person_model->get_persons_with_first_name($this->input->post('first_name'));			$this->load->view('person/view_all',$data);		}		else		{			$this->load->helper('form');			$this->load->view('person/search');		}	}		public function add()	{		if($this->input->post())		{			$this->load->model('person_model');			$this->person_model->insert_person($this->input->post('first_name'),					$this->input->post('last_name'), $this->input->post('age'));			$this->load->helper('url');			redirect('person/view_all');		}		else		{			$this->load->helper('form');			$this->load->view('person/add');		}	}		public function createXML() {		$this->load->model(‘person_model’);		$persons = $this->person_model->get_all_persons();		$xml = new SimpleXMLElement('<?xml version="1.0" encoding="utf-8"?><persons></persons>');		foreach($persons as $person)		{				$person = $xml->addChild('person');				$person->addChild('first_name', $person->first_name);				$person->addChild('last_name', $person->first_name);				$person->addChild('age', $person->age);		}		echo $xml->asXML();   }}